import gmpy2
from gmpy2 import mpz


def factor(N):
    """
    :param N: mpz(6)
    :return : mpz(p), mpz(q)
    """
    N = gmpy2.mpz(N)
    for i in range(1, 2 ** 20):
        A = gmpy2.isqrt(N) + i  # ??i??1???0????????
        x = gmpy2.isqrt(A ** 2 - N)
        p = A - x
        q = A + x
        if p * q == N:
            return p, q 

def fermat(n):
    n = gmpy2.mpz(n)
    x = gmpy2.ceil(gmpy2.sqrt(n))
    y = x**2 - n
    while not gmpy2.sqrt(y).is_integer():
        x += 1
        y = x**2 - n
    P = x + gmpy2.sqrt(y) 
    Q = x - gmpy2.sqrt(y)
    return(P, Q)


